import { Form, Tabs, Avatar, Input, Select, Col, Row, Button, Modal } from 'antd'
import { EnvironmentOutlined, PlusCircleOutlined, SearchOutlined, UserOutlined } from '@ant-design/icons';
import './Miempresa.scss'
import { Option } from 'antd/lib/mentions';
import { Link } from 'react-router-dom';
import Sucursales from '../../components/Sucursales/Sucursales';
//import ModalEvo from '../../components/Modal/Modal';


const sucursales = [];

export default function Miempresa() {
    
const { TabPane } = Tabs;
const [modal, setModal] = useState(false);

function openModal(parameter, event){
    alert('hghg');

}

const clickMe = (parameter) => (event) => {
    alert('hghg');
}

//const openModal = (parameter, event) => {

    //alert('hghg');
    // if(type === 1){
    //     const data = [{
    //         title: "Agregar sucursar",
    //         type: "form",
    //         items: [{
    //             input: "text",
    //             name: 'sucursalName',
    //             id: 'sucursalName',
    //             label: "Nombre de la sucursal",
    //             placeholder: "Agrega un nombre descriptivo para está sucursal",
    //             icon: false
    //         }, {
    //             input: "text",
    //             name: 'sucursalFrom',
    //             id: 'sucursalFrom',
    //             label: "Dirección de la sucursal",
    //             placeholder: "Agrega la dirección de la sucursal",
    //             icon: "EnvironmentOutlined"
    //         }]
    //     }];
    //     console.log(data);
        //setModal(true);
    //}

//}

const closeModal = () => {
    setModal(false);
}

const sendSucursal = () => {
   
    let nombreSucursal = document.getElementById('nombre').value;
    let direccionSucursal = document.getElementById('direccion').value;    

    sucursales.push({"nombre" : nombreSucursal, "direccion" : direccionSucursal});
    console.log(sucursales);

   
    setModal(false);
}

const info = () => {
  
    return(
        <>
        <span><b>Actividad de la empresa:</b></span><br/>
        <span>Lorem Ipsum es simplemente el texto de relleno
        de las imprentas y archivos de texto. Lorem
        Ipsum ha sido el texto de relleno estándar de las
        industrias desde el año 1500</span>
        </>
    );
   
}

const infosub = () => {
  
    return(
        <>
        <span><b>Subactividad de la empresa:</b></span><br/>
        <span>Lorem Ipsum es simplemente el texto de relleno
        de las imprentas y archivos de texto. Lorem
        Ipsum ha sido el texto de relleno estándar de las
        industrias desde el año 1500</span>
        </>
    );
   
}


    return (
        <Tabs className="tab" type="card">
            <TabPane className="tabPane" tab="Información" key="1">
                <div className="info">
                    
                    <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                        <Col  gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} span={12}>
                        <h2 style={{textAlign: "left"}}>Información de Mi empresa</h2>
                    <br/>
                            <Form name="form" layout="vertical">
                            <p><Avatar size={64} icon={<UserOutlined />} /></p>

                            <div className="formWork">
                            <Form.Item name="empresa" label="Nombre de Mi empresa" >
                                <Input type="text" placeholder="Nombre" />
                            </Form.Item>
                            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                                <Col className="gutter-row" span={12}>
                                
                                    <Form.Item name="empresa" label="Actividad de la empresa" tooltip={info}>
                                        <Select placeholder="Selecciona una opción" >
                                            <Option value="jack">Jack</Option>
                                            <Option value="lucy">Lucy</Option>
                                            <Option value="tom">Tom</Option>
                                        </Select>
                                    </Form.Item>
                                </Col>
                                <Col className="gutter-row" span={12}>
                                    <Form.Item name="empresa" label="Subactividad de la empresa" tooltip={infosub} >
                                        <Select placeholder="Selecciona una opción" >
                                            <Option value="jack">Jack</Option>
                                            <Option value="lucy">Lucy</Option>
                                            <Option value="tom">Tom</Option>
                                        </Select>
                                    </Form.Item>
                                </Col>
                            </Row>
                                
                            
                            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                                <Col className="gutter-row" span={12}>
                                    <Form.Item name="empresa" label="Tamaño de la empresa" >
                                        <Select placeholder="Selecciona una opción" >
                                            <Option value="jack">Jack</Option>
                                            <Option value="lucy">Lucy</Option>
                                            <Option value="tom">Tom</Option>
                                        </Select>
                                    </Form.Item>
                                </Col>

                                <Col  gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} span={12}>
                                    <Form.Item name="empresa" label="Pais" >
                                        <Select className="gutter-row" placeholder="Selecciona una opción" >
                                            <Option value="jack">Jack</Option>
                                            <Option value="lucy">Lucy</Option>
                                            <Option value="tom">Tom</Option>
                                        </Select>
                                    </Form.Item>
                                </Col>
                            </Row>
                                
                        
                            <Form.Item name="empresa" label="Sitio web" >
                                <Input type="text" placeholder="Nombre" />
                            </Form.Item>
                            <Form.Item name="empresa" label="Nombre de las oficinas centrales" >
                                <Input type="text" placeholder="Nombre" />
                            </Form.Item>
                            <Form.Item name="empresa" label="Dirección de las oficinas centrales" >
                                <Input addonAfter={<EnvironmentOutlined />} />
                            </Form.Item>
                            </div>
                            
                        </Form>
                        </Col>
                        <Col className="gutter-row dividerLeft" span={12}>
                            <h2 style={{textAlign: "left"}}>Agregar sucursal</h2>
                            <br/>
                            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                                <Col className="gutter-row" span={12}>
                                    <Form name="form" layout="vertical">
                                        <Form.Item name="empresa">
                                            <Input addonAfter={<SearchOutlined />} />
                                        </Form.Item>
                                    </Form>
                                </Col>
                                <Col className="gutter-row" span={12}>
                                    <Link to="#" onClick={this.openModal.bind(this, "1")} > <PlusCircleOutlined /> Agregar sucursal</Link>
                                </Col>
                            </Row>
                            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} >
                                <Col className="gutter-row" span={12}>
                                    <span>Sucursales Agregadas</span>
                                </Col>
                                <Col className="gutter-row" style={{textAlign: "left"}} span={12}>
                                    <span>Dirección</span>
                                </Col>
                                <div className="divider">
                                    <div className="dividerBottom"></div>
                                </div>
                            </Row>
                            <div id="sucursales" className="sucursales">
                                <Sucursales data={sucursales}/>
                            </div>
                            <Modal title="Agregar sucursal" visible={modal} onCancel={closeModal} onOk={sendSucursal}>
                                    <Form layout="vertical">
                                        <Form.Item name="empresa" label="Nombre de la sucursal" >
                                            <Input type="text" id="nombre" placeholder="Agrega un nombre descriptivo para está sucursal" />
                                        </Form.Item>
                                        <Form.Item name="direccion" label="Dirección de la sucursal" >
                                            <Input addonAfter={<EnvironmentOutlined />} id="direccion" placeholder="Agrega la dirección de la sucursal" />
                                        </Form.Item>
                                    </Form>
                                </Modal>
                        </Col>                        
                    </Row>
                   
                    <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                        <Col style={{textAlign: "right"}} className="gutter-row" span={24}>
                            <Button className="secondary" style={{marginRight: "15px"}}>Cancelar</Button>
                            <Button className="primary">Guardar</Button>
                        </Col>
                        <br/>
                    </Row>
                    
                </div>
                
            </TabPane>
            <TabPane  className="tabPane" tab="Cultura" key="2">
            Content of Tab Pane 2
            </TabPane>
        </Tabs>
       
    )
}
