import React, { useEffect, useState } from 'react'
import { Button, Col, Input, Row, Select, Form, Modal, Checkbox, AutoComplete, Dropdown, Menu } from 'antd';
import { IoMdAdd } from "react-icons/io";
import { SearchOutlined, UserOutlined } from '@ant-design/icons';
import { BsFillGrid3X2GapFill, BsListUl } from "react-icons/bs";
import { RiFilterFill } from "react-icons/ri";
import Avatar from '../../assets/img/avatar.png';
import Simplecard from '../../components/Cards/Simplecard'
import { Link } from 'react-router-dom';
import More from '../../assets/img/iconos/more_vert-24px.svg'
import { map } from 'lodash';
import './Equipos.scss';
import Miembrosequipo from '../../components/Miembrosequipo/Miembrosequipo';

const { Option } = Select;

export default function Equipos() {

    const NewTeam = [{name: "pruebita"}];
    const [newTeam, setNewTeam] = useState(false);

    const addMiembro = (title) =>{
        NewTeam.push({name:title });
        setNewTeam(true);
    }

    const renderTitle = (title) => (
        <span>{title}</span>
      );

    const renderItem = (title, count) => ({
        value: title,
        label: (
          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
            }}
            onClick={(e) => addMiembro(title, e)}
          >
            {title}
            <span>
                <UserOutlined /> {count}
            </span>
          </div>
        ),
      });

    const options = [
        {
          label: renderTitle('Area de marketing'),
          options: [renderItem('Panchita Lopez'), renderItem('Juan Charrasquiado')],
        },
        {
          label: renderTitle('Area de Gerencias'),
          options: [renderItem('Pepe Pecas'), renderItem('Juan Camaney')],
        },
        {
          label: renderTitle('Area de Ventas'),
          options: [renderItem('Pepe Grillo'), renderItem('Pinocho')],
        },
      ];


    

    const equipos = [
        {id: 1,
         nombre: "Equipo - Proyecto EVOU", 
         subtitle: "Mandos Medios",
         Tipo: 0,
         miembros: [
                {
             id: 1,
             nombre: "Giovanni Funentes",
             puesto: "Director RH",
             lider: true,
             rol: ""
            },
            {
                id: 2,
                nombre: "Panchita Lopez",
                puesto: "Directora de Arguendes",
                lider: false,
                rol: ""
            },
            {
                id: 3,
                nombre: "Jose Flores",
                puesto: "Director seguridad",
                lider: false,
                rol: ""
            }
        ],
        modalidad: 0,
        marcco: 0,
        fecha: "15-JUN-2021"
        }
    ];

    const equipos2 = [];
    //console.log(equipos);

    const [modalCreateTeam, setModalCreateTeam] = useState(false);
    const closeModalCreateTeam = () => {setModalCreateTeam(false);}
    const openModalCreateTeam = () => {setModalCreateTeam(true);}
    const sendCreateTeam = () => {setModalCreateTeam(false);}
    const ready = () => {alert('enviando datos');setModalCreateTeam(false);}

    // ------------- states formulario -----------------
    const [teamName, setteamName] = useState();
    const [tipo, setTipo] = useState();
    const [colaboradores, setColaboradores] = useState();
    const [modalidad, setModalidad] = useState();


    //----------------------------------------------------

    const info = () => {
        return(
            <>
            <span><b>Actividad de la empresa:</b></span><br/>
            <span>Lorem Ipsum es simplemente el texto de relleno
            de las imprentas y archivos de texto. Lorem
            Ipsum ha sido el texto de relleno estándar de las
            industrias desde el año 1500</span>
            </>
        );
    }

    const showFrame = (dato) =>{
        if(dato === "1"){
            document.getElementById('frame').setAttribute('style', "display: flex;");
        }else{
            document.getElementById('frame').setAttribute('style', "display: none;");
        }
        
    }

    const [Item, setItem] = useState('');
   
    const [value, setValue] = useState('');

    const [key, setKey] = useState(0);

    const setDates = (item, e) => {     
        setItem(item);
        setValue(equipos[item].nombre);
        setKey(item);
    }

       

    const clone = () => {
        

    }

    const rename = () => {
      equipos[Item].nombre = value;
      document.getElementById('nombreTeam').innerHTML= value;
      setmodalRename(false);
    }

    const edit = () => {

        setModalTarget(false);
        setModalCreateTeam(true);
    }

    const deleteTeam = () => {


    }

    const [modalRename, setmodalRename] = useState(false);
    
    const closeModalRename = () => {
        setmodalRename(false);
    }

    const openModalRename = () => {       
        setmodalRename(true);
        setValue(equipos[Item].nombre);
    }


    const done = () => {

        // let nameTeam = document.getElementById("nameTeam");
        // console.log(nameTeam);  
        // nameTeam.value = equipos[Item].nombre;
    }

    const onchange = (e) => {
        setValue(e.target.value);
        setKey(key);
    }

  // ------------ modal target ----------------
    const [modalTarget, setModalTarget] = useState();

    const openModalTarget = (index,data,e) => {
        setValue(data.nombre);
        setModalTarget(true);
    }

    const closeModalTarget = () => {
        setModalTarget(false);
    }

    const menu = (
        <Menu>
          <Menu.Item key="0">
            <Menu.Item key="3"><Link onClick={edit} to="#">Editar datos</Link></Menu.Item>
          </Menu.Item>
          <Menu.Divider />
          <Menu.Item key="0">
            <Menu.Item key="3"><Link to="#">Eliminar</Link></Menu.Item>
          </Menu.Item>
        </Menu>
      );

      const fruits = ["apple", "orange", "cherry","jhjhj"];

    return (
        <>  
             <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} >
                <Col className="gutter-row iconGray" span={1}><RiFilterFill /></Col>
                <Col className="gutter-row iconGray rigth" span={1}><span><BsFillGrid3X2GapFill /></span></Col>
                <Col className="gutter-row iconGray" span={3} style={{textAlign: "left"}}><BsListUl className="dividerLeft" style={{ paddingLeft: "5px", fontSize: "20px"}} /></Col>
                <Col className="gutter-row" span={15}><Input style={{width: "200px", marginTop: "-5px"}} addonAfter={<SearchOutlined />} placeholder="Buscar puesto" /></Col>
                <Col className="gutter-row" span={4}><Button className="primaryB" onClick={openModalCreateTeam} style={{textAlign: "left", marginRight: "20px", marginTop: "-5px"}} icon={<IoMdAdd className="iconAjust2"/>} > <span className="textAjust4">Nuevo Equipo</span></Button></Col>
