import React, { useState } from 'react'
import { Button, Col, Input, Row, Select, Form, Modal, Checkbox, AutoComplete, Dropdown, Menu } from 'antd';
import { IoMdAdd } from "react-icons/io";
import { SearchOutlined, UserOutlined } from '@ant-design/icons';
import { BsFillGrid3X2GapFill, BsListUl } from "react-icons/bs";
import { RiFilterFill } from "react-icons/ri";
import Avatar from '../../assets/img/avatar.png';
import Simplecard from '../../components/Cards/Simplecard'
import { Link } from 'react-router-dom';
import More from '../../assets/img/iconos/more_vert-24px.svg'
import './Equipos.scss';
import Miembrosequipo from '../../components/Miembrosequipo/Miembrosequipo';

const { Option } = Select;

export default function Equipos() {

    const addMiembro = () =>{
        //NewTeam.push();
    }

    const renderTitle = (title) => (
        <span>{title}</span>
      );

    const renderItem = (title, count) => ({
        value: title,
        label: (
          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
            }}
            onClick={addMiembro}
          >
            {title}
            <span>
                <UserOutlined /> {count}
            </span>
          </div>
        ),
      });

    const options = [
        {
          label: renderTitle('Area de marketing'),
          options: [renderItem('Panchita Lopez'), renderItem('Juan Charrasquiado')],
        },
        {
          label: renderTitle('Area de Gerencias'),
          options: [renderItem('Pepe Pecas'), renderItem('Polo Polo')],
        },
        {
          label: renderTitle('Area de Ventas'),
          options: [renderItem('Pepe Grillo'), renderItem('Pinocho')],
        },
      ];

    const NewTeam = [];

    

    const equipos = [
        {id: 1,
         nombre: "Equipo - Proyecto EVOU", 
         subtitle: "Mandos Medios",
         Tipo: 0,
         miembros: [
                {
             id: 1,
             nombre: "Giovanni Funentes",
             puesto: "Director RH",
             lider: true,
             rol: ""
            },
            {
                id: 2,
                nombre: "Panchita Lopez",
                puesto: "Directora de Arguendes",
                lider: false,
                rol: ""
            },
            {
                id: 3,
                nombre: "Jose Flores",
                puesto: "Director seguridad",
                lider: false,
                rol: ""
            }
        ],
        modalidad: 0,
        marcco: 0,
        fecha: "15-JUN-2021"
        }
    ];

    console.log(equipos);

    const [modalCreateTeam, setModalCreateTeam] = useState(false);
    const closeModalCreateTeam = () => {setModalCreateTeam(false);}
    const openModalCreateTeam = () => {setModalCreateTeam(true);}
    const sendCreateTeam = () => {setModalCreateTeam(false);}
    const ready = () => {alert('enviando datos');setModalCreateTeam(false);}

    // ------------- states formulario -----------------
    const [teamName, setteamName] = useState();
    const [tipo, setTipo] = useState();
    const [colaboradores, setColaboradores] = useState();
    const [modalidad, setModalidad] = useState();


    //----------------------------------------------------

    const info = () => {
        return(
            <>
            <span><b>Actividad de la empresa:</b></span><br/>
            <span>Lorem Ipsum es simplemente el texto de relleno
            de las imprentas y archivos de texto. Lorem
            Ipsum ha sido el texto de relleno estndar de las
            industrias desde el ao 1500</span>
            </>
        );
    }

    const showFrame = (dato) =>{
        if(dato === "1"){
            document.getElementById('frame').setAttribute('style', "display: flex;");
        }else{
            document.getElementById('frame').setAttribute('style', "display: none;");
        }
        
    }

    const [Item, setItem] = useState('');
   
    const [value, setValue] = useState('');

    const [key, setKey] = useState(0);

    const setDates = (item, e) => {     
        setItem(item);
        setValue(equipos[item].nombre);
        setKey(item);
        console.log("key: " + key);
        console.log("Item: " + Item);
        console.log("value: " + value);
    }

    const clone = () => {
        
        // equipos.push({id: "1", nombre: "Equipo - Proyecto EVOU", subtitle: "Mandos Medios", fecha: "15-JUN-2021"});
        // console.log(equipos);
    }

    const rename = () => {
      equipos[Item].nombre = value;
      document.getElementById('nombreTeam').innerHTML= value;
      setmodalRename(false);
    }

    const edit = () => {


        setModalCreateTeam(true);
    }

    const deleteTeam = () => {


    }

    const [modalRename, setmodalRename] = useState(false);
    
    const closeModalRename = () => {
        setmodalRename(false);
    }

    const openModalRename = () => {       
        setmodalRename(true);
        setValue(equipos[Item].nombre);
        console.log();
    }


    const done = () => {

        // let nameTeam = document.getElementById("nameTeam");
        // console.log(nameTeam);  
        // nameTeam.value = equipos[Item].nombre;
    }

    const onchange = (e) => {
        setValue(e.target.value);
        setKey(key);
        console.log(key);
        
    }

  // ------------ modal target ----------------
    const [modalTarget, setModalTarget] = useState();

    const openModalTarget = (index,data,e) => {
        setValue(data.nombre);
        setModalTarget(true);
    }

    const closeModalTarget = () => {
        setModalTarget(false);
    }

    const menu = (
        <Menu>
          <Menu.Item key="0">
            <Menu.Item key="3"><Link to="/miempresa-edit">Editar datos</Link></Menu.Item>
          </Menu.Item>
        </Menu>
      );


    return (
        <>  
             <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} >
                <Col className="gutter-row iconGray" span={1}><RiFilterFill /></Col>
                <Col className="gutter-row iconGray rigth" span={1}><span><BsFillGrid3X2GapFill /></span></Col>
                <Col className="gutter-row iconGray" span={3} style={{textAlign: "left"}}><BsListUl className="dividerLeft" style={{ paddingLeft: "5px", fontSize: "20px"}} /></Col>
                <Col className="gutter-row" span={15}><Input style={{width: "200px", marginTop: "-5px"}} addonAfter={<SearchOutlined />} placeholder="Buscar puesto" /></Col>
                <Col className="gutter-row" span={4}><Button className="primaryB" onClick={openModalCreateTeam} style={{textAlign: "left", marginRight: "20px", marginTop: "-5px"}} icon={<IoMdAdd className="iconAjust2"/>} > <span className="textAjust4">Nuevo Equipo</span></Button></Col>
            </Row>

            <Row style={{marginTop: "10px", paddingLeft: "15px"}}>
                <Simplecard openModalTarget={openModalTarget} delete={deleteTeam} edit={edit} clone={clone} openModalRename={openModalRename} setDates={setDates} equipos={equipos} />
            </Row>
                
            <div style={{height: "50px"}}></div>
            

            <Modal title="Crear Equipo" className="largeModal" visible={modalCreateTeam} onCancel={closeModalCreateTeam} onOk={sendCreateTeam} footer={[<span style={{marginRight:"120px"}}><Link to="#">Aqu</Link> puedes ver buenas prcticas en equipos Agile</span>,<Button style={{marginRight: "20px", width: "100px"}} className="secondary" onClick={closeModalCreateTeam} >Cancelar</Button>, <Link to="/equipos"><input type="button" className="primary" style={{width: "100px"}} onClick={ready} value="Crear" /> </Link>]} >
                    <Form name="form" layout="vertical">

                        <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                            <Col className="gutter-row" span={12}>
                                <Form.Item name="site" label="Nombre del equipo" >
                                    <Input type="text" onChange={onchange}  defaultValue={value} value={value} placeholder="Aqui puedes asignar el nombre de tu equipo" />
                                </Form.Item>
                            </Col>
                            <Col className="gutter-row" span={12}>
                                <Form.Item name="activity" label="Tipo de equipo" tooltip={info}>
                                    <Select placeholder="Selecciona una opcin" onchange={() => {}} defaultValue={() => {}} onChange={showFrame} >
                                        <Option value="0">Personalizado</Option>
                                        <Option value="1">Modo Opertaivo Agile</Option>
                                    </Select>
                                </Form.Item>
                            </Col>
                        </Row>

                        <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                            <Col className="gutter-row" span={12}>
                                <Form.Item name="empresa" label="Agregar colaboradores">

                                <AutoComplete
                                    dropdownClassName="certain-category-search-dropdown"
                                    dropdownMatchSelectWidth={300}
                                    options={options}
                                >
                                    <Input.Search size="large" placeholder="Buscar colaboradores" />
                                </AutoComplete>
                                   
                                </Form.Item>
                            </Col>
                            <Col className="gutter-row" span={12}>
                                <Form.Item name="modalidad" label="Modalidad" tooltip={info}>
                                    <Select placeholder="Selecciona una opcin" >
                                        <Option value="jack">Funcional</Option>
                                        <Option value="lucy">Lucy</Option>
                                        <Option value="tom">Tom</Option>
                                    </Select>
                                </Form.Item>
                            </Col>
                        </Row>
                    
                        <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} id="frame" style={{display: "none"}}  >
                            <Col className="gutter-row" span={12}></Col>
                            <Col className="gutter-row" span={12}>
                                <Form.Item name="frame" label="Marco de trabajo" tooltip={info}>
                                    <Select placeholder="Selecciona una opcin" >
                                        <Option value="jack">SCRUM</Option>
                                        <Option value="lucy">Lucy</Option>
                                        <Option value="tom">Tom</Option>
                                    </Select>
                                </Form.Item>
                            </Col>
                        </Row>                    

                        <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} style={{textAlign: "center", marginTop: "10px"}}>
                            <Col className="gutter-row" span={8}>Miembros del equipo</Col>
                            <Col className="gutter-row" span={4}>Lider</Col>
                            <Col className="gutter-row" span={6}>Rol en el equipo</Col>
                            <Col className="gutter-row" span={6}>Acciones</Col>
                        </Row>

                        

                       

                        <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} style={{textAlign: "center", marginTop: "10px"}}>

                            {/* <Miembrosequipo /> */}
                        </Row>

                    </Form>
            </Modal>

           
            <Modal title="Renombre de equipo" className="smallModal2" visible={modalRename} onCancel={closeModalRename} footer={[<Button style={{marginRight: "20px", width: "100px"}} className="secondary" onClick={closeModalRename} >Cancelar</Button>, <Link to="/equipos"><input type="button" className="primary" style={{width: "100px", marginRight: "40px"}} onClick={rename} value="Guardar" /> </Link>]} >

                <Form name="form1" layout="vertical">
                    <Form.Item name="nameTeam" >
                        <Input key={key} type="text" onChange={onchange}  defaultValue={value} value={value} />
                    </Form.Item>
                </Form>
                
            </Modal>

            <Modal title={value} visible={modalTarget} onCancel={closeModalTarget} footer="" >

                <Row>
                    <Col span={6}>
                        <p>Tipo de equipo</p>
                    </Col>
                    <Col span={16} >
                        <p>Modalidad</p>
                    </Col>
                    <Col span={2}>
                        <Dropdown overlay={menu} trigger={['click']}>
                            <Link to="#" className="ant-dropdown-link" onClick={e => e.preventDefault()}><img alt="logo" src={More} width="25" height="25" /></Link>
                        </Dropdown>
                    </Col>
                </Row>
                <Row>
                    <Col span={6}>
                        <p>Tipo de equipo</p>
                    </Col>
                    <Col span={16} >
                        <p>Modalidad</p>
                    </Col>
                    <Col span={2}>
                        <Dropdown overlay={menu} trigger={['click']}>
                            <Link to="#" className="ant-dropdown-link" onClick={e => e.preventDefault()}><img alt="logo" src={More} width="25" height="25" /></Link>
                        </Dropdown>
                    </Col>
                </Row>
                
            </Modal>                    
            
            
        </>
    )   
}
