import React from "react";
import { Card } from 'antd'
import 'antd/dist/antd.css';
import Item from 'antd/lib/list/Item';
import Carousel from 'react-elastic-carousel'
import { LeftOutlined } from '@ant-design/icons';
import './Screen.scss'
import { Link } from "react-router-dom";
import ReactElasticCarousel from "react-elastic-carousel";


export default function Screen() {

    return (
        <div className="screen">
            <Card title="¿Por qué crear un Organigrama?" headStyle={{ "text-align" : "center"}} bordered={false} style={{ width: 400, height: 400 }}>
            <Carousel itemsToShow={1}>
                <Item>
                    <div className="carruselItem">
                    <p>Podrás mejora la organización y definición de los ejes principales en la toma de decisiones.</p>
                    <p>Divide y segmenta el trabajo por unidades tácticas, mejorando la gestión de la estrategia.</p>
                    <p>Visualiza el vínculo entre el alcance del puesto y el talento del colaborador cerrando las brechas de las áreas de acción de la compañías así como las responsabilidades de los miembros que la forman.</p>
                    <p>Optimiza y facilita la gestión de los procesos entre EVOU y Evaluatest</p>
                    </div>
                </Item>
                <Item>2</Item>
                <Item>3</Item>
               
            </Carousel>
            <Link to=""><LeftOutlined /></Link>
            </Card>
           
        </div>
       
    )
}
