import React, { useEffect, useState } from 'react'
import { Button, Col, Input, Row, Form, Modal, Select } from 'antd'
import { IoMdAdd } from "react-icons/io";
import { SearchOutlined } from '@ant-design/icons';

import Checkbox from 'antd/lib/checkbox/Checkbox';
import { Link } from 'react-router-dom';
import { BsFillGrid3X2GapFill, BsListUl } from "react-icons/bs";
import { RiFilterFill } from "react-icons/ri";

import CreateTeam from './components/CreateTeam';

const LOCAL_STORAGE_KEY = "lista";
const { Option } = Select;

export default function ModalCreateTeam() {

    const [modalCreateTeam, setModalCreateTeam] = useState(false);
    const closeModalCreateTeam = () => {setModalCreateTeam(false);}
    const openModalCreateTeam = () => {setModalCreateTeam(true);}
    const sendCreateTeam = () => {setModalCreateTeam(false);}
    const ready = () => {alert('enviando datos');setModalCreateTeam(false);}

    const [ datos, setDatos ] = useState([]);

    useEffect(() => {
        // fires when app component mounts to the DOM
        const storageTodos = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
        if (storageTodos) {
            setDatos(storageTodos);
        }
      }, []);

    useEffect(() => {
        // fires when todos array gets updated
        localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(datos));
      }, [datos]);

    const agrega = (dato) => {
        setDatos([dato, ...datos])
    }

      function removeDato(id) {
        setDatos(datos.filter(dato => dato.id !== id));
      }

    return (
        <CreateTeam modalCreateTeam={modalCreateTeam} closeModalCreateTeam={closeModalCreateTeam} openModalCreateTeam={openModalCreateTeam} agrega={agrega} datos={datos} removeDato={removeDato} />
    )
}
