//import React, { Component } from 'react'
import React, { useState } from 'react'
import { Col, Dropdown, Input, Menu, Row, Form, Select, Button } from 'antd'
import More from '../../assets/img/iconos/more_vert-24px.svg'
import { Link } from 'react-router-dom'
import { RiPencilFill } from 'react-icons/ri';
import { PlusCircleOutlined } from '@ant-design/icons';
import Modalcompetencias from '../../components/Modalcompetencias/Modalcompetencias';
import Screendefault from '../../components/Screens/Screendefault';
import Slidercompetencias from '../../components/Slider/Slidercompetencias';
import Modal from 'antd/lib/modal/Modal';
import Checkbox from 'antd/lib/checkbox/Checkbox';
//import SortableTree from "react-sortable-tree";

const { Option } = Select;

export default function AreasypuestosAddJob() {
    

    // const treeData = [
    //     { title: "Chicken", expanded: true, 
    //     children: [{ title: "Egg" }, { title: "Ohh" }] }
    //     ];

    const [slider, setSlide] = useState(50);
    const [action, setAction] = useState(false);
    const [modalComp, setModalComp] = useState(false);
    const [Competencia, setCompetencia] = useState(false);
    const [Item, setItem] = useState(false);
    const [Permisos, setPermisos] = useState(false);

    const competencias = [];

    const openModalComp = () => {
        setSlide(50);
        setAction(2);
        setModalComp(true);
    }

    const sendComp = () => {alert('ok'); setModalComp(false);}
    const closeModalComp= () => {setModalComp(false);}

    const cefective = (competencia , e) => {
        setCompetencia(competencia);
        document.getElementById("titulo").innerHTML = competencia;
        let cefective = document.getElementById("cefective");
        cefective.setAttribute("style", "display: flex;");
        let screen = document.getElementById("screen");
        screen.setAttribute("style", "display: flex;");
    }

    const onChangeInput = (inputValue) =>{
        setSlide(inputValue*25);
    }

    const onChange = (inputValue) => {
        document.getElementById('nivel').value = (inputValue/25);
        setSlide(inputValue);
    }

    const closecefective = () => {
        let screen = document.getElementById("screen");
        screen.setAttribute("style", "display: none;");
        let next = document.getElementById("next");
        next.setAttribute("style", "display: flex;");
        let cefective = document.getElementById("cefective");
        cefective.setAttribute("style", "display: none;");
    }

    const closeInfo = () => {
        if(action !== 0){competencias.push({"competencia" : Competencia, "nivel" : (slider/25)});}
        let screen = document.getElementById("screen");
        screen.setAttribute("style", "display: none;");
        setModalComp(false);
    }

    const editInfo = () => {
        competencias[Item].competencia = Competencia;
        competencias[Item].nivel = (slider/25); 
        document.getElementById('nomCompe' +  Item).innerHTML = Competencia;
        document.getElementById('nivel' + Item).innerHTML = (slider/25);
        let screen = document.getElementById("screen");
        screen.setAttribute("style", "display: none;");
        setModalComp(false);
    }

          //console.log(treeData);
    
    // const changeTreeData = () => {
    //     alert('change');
    // }
    
    const marks = {
        0: '0',
        25: '1',
        50: '2',
        75: '3',
        100: '4',
    };

    const closeModalPermisos = () => {
        setPermisos(false);
    }

    const sendPermisos = () => {
        alert('permisos enviados');
    }

    const addPermisos = () => {
        setPermisos(true);
    }

    const menu = (
        <Menu>
          <Menu.Item key="0">
            <Menu.Item key="3"><Link to="#" onClick={addPermisos}>Agregar permisos</Link></Menu.Item>
          </Menu.Item>
          <Menu.Item key="0">
            <Menu.Item key="3"><Link to="/miempresa-edit">Eliminar puesto</Link></Menu.Item>
          </Menu.Item>
        </Menu>
      );
   

    return (
        <div>
            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}  >
                <Col className="gutter-row" span={14}  >
                    <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                        <Col className="gutter-row" span={22} style={{textAlign: "left", marginBottom: "30px"}}>
                            <h3>Datos de Mi empresa</h3>
                            <span>Completa la información del puesto</span>
                        </Col>
                        <Col className="gutter-row" span={2}>
                            <Dropdown overlay={menu} trigger={['click']}>
                                <Link to="#" className="ant-dropdown-link" onClick={e => e.preventDefault()}><img alt="logo" src={More} width="25" height="25" /></Link>
                            </Dropdown>
                        </Col>
                    </Row>
                    <div className="formWork">
                        <Form layout="vertical">
                            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                                <Col className="gutter-row" span={24}>
                                    <Form.Item name="password" label="nombre del puesto" >
                                        <Input type="text" id="nombre" placeholder="Ej. Director General A" />
                                    </Form.Item>
                                </Col>
                            </Row>



                            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} style={{marginTop: "-20px"}} >
                                <Col className="gutter-row" span={12}  >
                                    <Select className="select" placeholder="Ej. Dirección" >
                                        <Option value="jack">Jack</Option>
                                        <Option value="lucy">Lucy</Option>
                                        <Option value="tom">Tom</Option>
                                    </Select>
                                </Col>
                                <Col className="gutter-row" span={12}  >
                                    <Select className="select"placeholder="Ej. Dirección" >
                                        <Option value="jack">Jack</Option>
                                        <Option value="lucy">Lucy</Option>
                                        <Option value="tom">Tom</Option>
                                    </Select>
                                </Col>
                            </Row>
                            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                                <Col className="gutter-row" span={12}    >
                                    <Select className="select" placeholder="Ej. Puesto clave" >
                                        <Option value="jack">Jack</Option>
                                        <Option value="lucy">Lucy</Option>
                                        <Option value="tom">Tom</Option>
                                    </Select>
                                </Col>
                                <Col span={12} className="gutter-row"  >
                                    <Select className="select" placeholder="Ej. Alto" >
                                        <Option value="jack">Jack</Option>
                                        <Option value="lucy">Lucy</Option>
                                        <Option value="tom">Tom</Option>
                                    </Select>
                                </Col>
                            </Row>
                            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                                <Col span={12}  className="gutter-row"  >
                                    <Select className="select" placeholder="Ej. Educación Superior" >
                                        <Option value="jack">Jack</Option>
                                        <Option value="lucy">Lucy</Option>
                                        <Option value="tom">Tom</Option>
                                    </Select>
                                </Col>
                                <Col span={12}  className="gutter-row"  >
                                    <Select className="select" placeholder="Selecciona una opción" >
                                        <Option value="jack">Jack</Option>
                                        <Option value="lucy">Lucy</Option>
                                        <Option value="tom">Tom</Option>
                                    </Select>
                                </Col>
                            </Row>

                            <Row style={{marginTop: "20px"}}>
                                <Col span={24}><span><b>Habilidades para el puesto</b></span></Col>
                            </Row>

                            <Row style={{marginTop: "20px"}}>
                                <Col span={24}>
                                    
                                </Col>
                            </Row>



                            <Row>
                                <Col span={24}>
                                <Form.Item name="habilidades" label="Habilidades tecnicas" >
                                    <Input type="text" id="habilidades" placeholder="Ej. Microsoft word" />
                                </Form.Item>
                                </Col>
                            </Row>
                            
                            <Row>
                                <Col span={24}>
                                <Form.Item name="idiomas" label="Idiomas" >
                                        <Input type="text" id="idiomas" placeholder="Ej. Ingles avanzado" />
                                    </Form.Item>
                                </Col>
                            </Row>

                            <Row className="dividerBottomFull">
                                <Col span={16} >Competencias institucionales</Col>
                                <Col span={8} >Nivel</Col>
                            </Row>

                            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} style={{marginTop: "10px"}}>
                                <Col span={16}>Creatividad</Col>
                                <Col span={6}>3</Col>
                                <Col span={2}></Col>
                            </Row>

                            <Row className="tagTwo" gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} style={{marginTop: "10px"}}>
                                <Col span={17}>Solución de Problemas - Desarrolla Alternativas</Col>
                                <Col span={5}>3</Col>
                                <Col span={2}><RiPencilFill style={{marginRight: "10px", marginTop: "7px"}}  /></Col>
                            </Row>

                            <Row className="dividerBottomFull" style={{marginTop: "20px"}}>
                                <Col span={16} >Competencias institucionales</Col>
                                <Col span={8} ><Link to="#" onClick={openModalComp} > <PlusCircleOutlined /> Agregar competencias</Link></Col>
                            </Row>

                            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} style={{marginTop: "10px"}}>
                                <Col span={16}>Actitud de servicio </Col>
                                <Col span={6}>3</Col>
                                <Col span={2}></Col>
                            </Row>

                            <Row className="tagTwo" gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} style={{marginTop: "10px"}}>
                                <Col span={17}>Apego a normas</Col>
                                <Col span={5}>3</Col>
                                <Col span={2}><RiPencilFill style={{marginRight: "10px", marginTop: "7px"}}  /></Col>
                            </Row>

                            <Row className="dividerBottomFull" style={{marginTop: "20px"}}>
                                <Col span={16} >Competencias del puesto</Col>
                                <Col span={8} ><Link to="#" onClick={openModalComp} > <PlusCircleOutlined /> Agregar competencias</Link></Col>
                            </Row>

                            <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }} style={{marginTop: "10px"}}>
                                <Col span={16}>Capacidad de aprender </Col>
                                <Col span={6}>3</Col>
                                <Col span={2}></Col>
                            </Row>

